import React, {useState} from 'react';
import TodoList from './components/TodoList';
import SubmitBtn from './components/SubmitBtn';


const TodoApp = _ => {
  const [todoItem, setTodoItem] = useState({
    todo : '',
    completed: false,
    totalNumOfCompleted: 0
  });

  const [todoList, setTodoList] = useState([]);
  const completedNumber = _ => {
    debugger;
    const completedTodos = todoList.filter( (todo, idx) => {
      return todo.completed === true;
    })
    return completedTodos.length;
  }
  return (
    <div className="todo-container">
      <h1 className="todo-title centered"> Tasks </h1>
      <TodoList
        todoList={todoList}
        setTodoList={setTodoList}
      />
      <SubmitBtn
        todoItem={todoItem}
        setTodoItem={setTodoItem}
        setTodoList={setTodoList}
        todoList={todoList}
      />
      <p>Completed: {todoList.length}</p>
      <p>Pending: {todoList.length}</p>
      <p>Total number of tasks in queue: {this.completedNumber}</p>
    </div>
  )
}

export default TodoApp;
