import React from 'react';

const Todo = ({todo, idx, todoList, setTodoList, todoItem}) => {
  const removeTodo = _ => {
    const newTodoList = todoList.filter( (todo, index) => {
      return index !== idx
    });
    setTodoList(newTodoList);

  }
  const completeTodo = _ => {
      const newCompletedState = todoList.map( (todoItem, index) => {
        if(index === idx) {
          return todoItem.completed = !todoItem.completed;
        }
      })
      setTodoList(newCompletedState);
  }

  return (
    <div className="todo-item">
      {todo}
      <div className="remove-btn" onClick={removeTodo}>remove</div>
      <div className="editBtn">edit</div>
      <div className="completeBtn" onClick={completeTodo}>complete</div>
    </div>
  );
}

export default Todo;
