import React from 'react';

const Todo = ({idx, todoList, setTodoList, todoItem}) => {
  const removeTodo = _ => {
    const newTodoList = todoList.filter( (todo, index) => {
      return index !== idx
    });
    setTodoList(newTodoList);

  }
  const completeTodo = _ => {
      const newCompletedState = todoList.map( (todoItem, index) => {
        if(index === idx) {
          return {
            todo: todoItem.todo,
            completed: !todoItem.completed
          }
        }

        return todoItem;
      })
      setTodoList(newCompletedState);
  }

  return (
    <div className="todo-item">
      {todoItem.todo}
      <div className="remove-btn" onClick={removeTodo}>remove</div>
      <div className="editBtn">edit</div>
      <div className="completeBtn" onClick={completeTodo}>{todoItem.completed ? 'completed': 'complete'}</div>
    </div>
  );
}

export default Todo;
