import React from 'react';

const Todo = ({todo, idx, todoList, setTodoList}) => {
  const removeTodo = _ => {
    const newTodoList = todoList.filter( (todo, index) => {
      return index !== idx
    });
    setTodoList(newTodoList);

  }
  return (
    <div className="todo-item">
      {todo}
      <div onClick={removeTodo}>close</div>
    </div>
  );
}

export default Todo;
