import React from 'react';

const Todo = ({idx, todoList, setTodoList, todoItem}) => {
  const removeTodo = _ => {
    const newTodoList = todoList.filter( (todo, index) => {
      return index !== idx
    });
    setTodoList(newTodoList);

  }

  const editTodoFocus = e => {
    e.currentTarget.setAttribute('contentEditable', true);

  }

  const editTodo = e => {
    console.log(e.target);
  }

  const completeTodo = _ => {
      const newCompletedState = todoList.map( (todoItem, index) => {
        if(index === idx) {
          return {
            todo: todoItem.todo,
            completed: !todoItem.completed
          }
        }

        return todoItem;
      })
      setTodoList(newCompletedState);
  }

  return (
    <div className="todo-item-wrapper">
      <label for={'todoItem' + idx} className="completed-checkbox">
        <input id={'todoItem' + idx} type="checkbox" className="completeBtn" checked={todoItem.completed ? true: false} onChange={completeTodo} />
        <span className="checkmark"></span>
      </label>
      <div onChange={(e) => editTodo(e)} onDoubleClick={(e) => editTodoFocus(e)} className={todoItem.completed ? 'todo-item completed-todo' : 'todo-item'} >
        {todoItem.todo}
        <div className="remove-btn" onClick={removeTodo}>remove</div>
      </div>
    </div>
  );
}

export default Todo;
