import React from 'react';

const SubmitBtn = ({todoItem, setTodoItem, setTodoList, todoList}) => {
  const handleSubmit = e => {
    e.preventDefault();
    if(todoItem.todo !== '') {
      const newTodoList = [...todoList, todoItem];
      setTodoList(newTodoList);
      setTodoItem({
          todo: '',
          completed: false
      });
    } else {
      console.log('cant submit an empty task');
    }
  };

  const handleInputChange = e => {
    e.preventDefault();
    const todoItm = {
      todo : e.target.value,
      completed : false
    }
    setTodoItem(todoItm)
  }

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        onChange = {handleInputChange}
        className="todo-input"
        value={todoItem.todo}
        placeholder="What needs to be done?"
      />
      <input
        className="submit-todo-btn btn"
        type="submit"
        value="submit"
      />
    </form>
  )
}

export default SubmitBtn;
